//import redux
import {combineReducers} from 'redux'
//import store con (gioHang)
import {GioHangReducer} from './GioHangReducer'
// store tổng ứng dụng (store= reducer)
import{BurgerReducer} from './BurgerReducer'
import {SinhVienReducer} from './SinhVienReducer'
import {XucXacReducer} from './XucXacReducer'
import BTBuaGiayKeoReducer from './BTBuaGiayKeoReducer'
import  {BtMovieReducer} from './BtMovieReducer'




export const rootReducer = combineReducers({
// nơi chứa các reducer cho nghiêp vụ (store con )
    // + reducer giohang = coi như chứa state của giỏ hàng 
    GioHangReducer,
    BurgerReducer,
    SinhVienReducer,
    XucXacReducer,
    BTBuaGiayKeoReducer,
    BtMovieReducer

})
